import React from "react";
import { connect } from 'react-redux';
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import { dashboard as dashboardRoutes, auth as authRoutes } from "./index";

import DashboardLayout from "../layouts/Dashboard";
import AuthLayout from "../layouts/Auth";
import Page404 from "../pages/auth/Page404";
import PrivateRoute from './AuthenticatedRoute';


const childRoutes = (RouteComponent, Layout, routes) => (
  routes.map(({ children, path, component: Component }, index) =>
    children ? (
      // Route item with children
      children.map(({ path, component: Component }, index) => (
        <RouteComponent
          key={index}
          path={path}
          exact
          render={props => (
            <Layout>
              <Component {...props} />
            </Layout>
          )}
        />
      ))
    ) : (
      // Route item without children
      <RouteComponent
        key={index}
        path={path}
        exact
        render={props => (
          <Layout>
            <Component {...props} />
          </Layout>
        )}
      />
    )
  )
);

const Routes = () => (
  <Router>
    <Switch>
      {childRoutes(PrivateRoute, DashboardLayout, dashboardRoutes)}
      {childRoutes(Route, AuthLayout, authRoutes)}
      <Route
        render={() => (
          <AuthLayout>
            <Page404 />
          </AuthLayout>
        )}
      />
    </Switch>
  </Router>
);

const mapStateToProps = state => ({
  isAuthenticated: state.users.isAuthenticated,
});

export default connect(mapStateToProps)(Routes);
